DROP DATABASE IF EXISTS ProyectoFinal;
CREATE DATABASE IF NOT EXISTS ProyectoFinal;
USE ProyectoFinal;

## CREACION DE TABLA DE USUARIOS
CREATE TABLE Usuarios (
    idUsuario INT AUTO_INCREMENT PRIMARY KEY,
    nombre VARCHAR(50) NOT NULL,
    contrasenia VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL UNIQUE,
	residencia VARCHAR(50),
    numTelf VARCHAR(15) NOT NULL UNIQUE
);

## CREACION DE TABLA DE ALOJAMIENTOS
CREATE TABLE Alojamientos (
    idAlojamiento INT AUTO_INCREMENT PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    imagen VARCHAR(255),
    imagen1 VARCHAR(255),
    imagen2 VARCHAR(255),
    tipo SET('Casa Rural', 'Hotel', 'Apartamento', 'Camping'),
    descripcion TEXT,
    descripcionlarga TEXT,
	servicios SET('Wifi', 'Cocina', 'Secadora', 'Calefacción', 'TV', 'Aire Acondicionado', 'Secador de Pelo'),
    prestaciones SET('Piscina', 'Aparcamiento gratuito', 'Cuna', 'Apto para fumadores', 'Barbacoa', 'Chimenea interior'), 
    localizacion VARCHAR(100) NOT NULL,
    region VARCHAR(50) NOT NULL,
    valoracion INT CHECK (valoracion >= 1 AND valoracion <= 5) NOT NULL,
    capacidad INT NOT NULL,
    precio DECIMAL(10 , 2 ) NOT NULL,
    numTelf VARCHAR(9) NOT NULL UNIQUE,
    numHabs INT NOT NULL,
    habsDisponibles BOOLEAN DEFAULT TRUE,
	lavabos INT NOT NULL DEFAULT 1
);

## CREACION DE TABLAS DE ACTIVIDADES
CREATE TABLE Actividades (
    idActividad INT AUTO_INCREMENT PRIMARY KEY,
    nombre VARCHAR(100) NOT NULL,
    imagen VARCHAR(255),
    imagen1 VARCHAR(255),
    imagen2 VARCHAR(255),
    localizacion VARCHAR(100) NOT NULL,
    region VARCHAR(50) NOT NULL,
    precio DECIMAL(10 , 2 ) NOT NULL,
    duracion INT NOT NULL,
    dificultad ENUM('Baja', 'Media', 'Alta') NOT NULL,
    numTelf VARCHAR(9) NOT NULL UNIQUE,
    tipo SET('Aventura','Visitar','Animales','Costeras'),
    categoria SET('Senderismo', 'Ciclismo de montaña', 'Turismo cultural', 'Pesca deportiva', 'Avistamiento de aves', 'Ecoturismo', 'Rutas a caballo', 'Agricultura y huertos', 'Fotografía de naturaleza', 'Observación astronómica', 'Escalada en roca'),
    descripcion TEXT,
    descripcionlarga TEXT,
    fechaInicio DATE,
    hora TIME,
    plazasDisponibles INT
);


## CREACION DE TABLA DE RESERVAS
CREATE TABLE Reserva (
    idReserva INT AUTO_INCREMENT PRIMARY KEY,
    fechaEntrada DATE NOT NULL,
    fechaSalida DATE NOT NULL,
    numPersonas INT NOT NULL,
    estadoReserva ENUM('Pendiente', 'Confirmada', 'Cancelada'),
    numReserva VARCHAR(20) UNIQUE,
	totalPrecio DECIMAL,
	idUsuario INT,
	idAlojamiento INT,
	idActividad INT,
	idComentario INT,
	FOREIGN KEY (idUsuario) REFERENCES Usuarios(idUsuario),
	FOREIGN KEY (idAlojamiento) REFERENCES Alojamientos(idAlojamiento),
	FOREIGN KEY (idActividad) REFERENCES Actividades(idActividad)
);

## CREACION DE TABLA DE TARJETA BANCARIA
CREATE TABLE TarjetaBancaria (
    idtarjeta INT AUTO_INCREMENT PRIMARY KEY,
    nombretitular VARCHAR(255),
    numerotarjeta VARCHAR(16),
	fechacaducidad DATE,
    cvv VARCHAR(3),
    idUsuario INT,
    FOREIGN KEY (idUsuario) REFERENCES Usuarios(idUsuario)
);


